mapper.select:
SELECT A.*
FROM DETECTION_LOG A
WHERE A.DETECTION_CODE = #{ patientCode }

mapper.insert:
INSERT
  INTO DETECTION_LOG A
VALUES
     (
       SEQ_DETECTION_CODE.NEXTVAL
     , #{ detectionDate }
     , #{ detectionLocation }
     , #{ patientCode }
     , #{ imagePath }
     , #{ imageName }
     )
